// n8n Code Node - Fast Message Type Analyzer
const msg = $input.first().json.output || '';

// Quick checks
const wordCount = msg.trim().split(/\s+/).length;
const hasList = /^\s*[-•*\d+\.]\s|[-•*]\s/m.test(msg);
const hasData = /https?:\/\/|@\w+|#\w+|\$\d+|\d+%|\d{3}-\d{3}-\d{4}/.test(msg);
const isShort = wordCount < 20;

// Simple decision logic
let sendAsAudio = false;

if (hasList || hasData || isShort) {
  sendAsAudio = false; // Send as text
} else if (wordCount > 50) {
  sendAsAudio = true;  // Send as audio
} else {
  // Medium length - check if conversational
  sendAsAudio = /\b(I think|I feel|you know|actually|so|well)\b/i.test(msg);
}

return {
  json: {
    sendAsAudio,
    sendAsText: !sendAsAudio,
    type: sendAsAudio ? 'audio' : 'text',
    wordCount
  }
};

// Get all inputs (each row is a separate input)
const allInputs = $input.all();
const config = {};

// Each input contains one config row
allInputs.forEach(input => {
  const row = input.json;
  config[row.key] = row.value;
});

return [{ json: { config } }];
