const bin = items[0].binary.data;
const fileName = bin.fileName || 'unknown.docx';

bin.fileName = fileName.replace(/\.docx$/i, '.zip');
bin.fileExtension = 'zip';
bin.mimeType = 'application/zip';

return [{
  json: {
    originalFileName: fileName // ✅ carry original filename in JSON
  },
  binary: {
    data: bin
  }
}];

// Pick_document_xml  (Run Once for All Items)
const result = [];

for (const file of Object.values(items[0].binary)) {
  if (file.fileName === 'document.xml') {
    result.push({ binary: { data: file } });
    break;            // found it, stop looping
  }
}

return result;

const xml = items[0].json.data;
const fileName = $('Compression').first().json.originalFileName || 'Unknown.docx';

const paraRegex = /<w:p[^>]*?>([\s\S]*?)<\/w:p>/g;
const wTRegex = /<w:t[^>]*?>(.*?)<\/w:t>/g;

// Minimal HTML entity decoding using DOM-level trick
function decodeHtmlEntities(str) {
  return str
    .replace(/&quot;/g, '"')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&apos;/g, "'")
    .replace(/&#39;/g, "'")
    .replace(/&nbsp;/g, ' ');
}

const paragraphs = [];
let pMatch;

while ((pMatch = paraRegex.exec(xml))) {
  const inner = pMatch[1];
  const parts = [];

  let tMatch;
  while ((tMatch = wTRegex.exec(inner))) {
    const decoded = decodeHtmlEntities(tMatch[1]);
    parts.push(decoded);
  }

  const text = parts.join('').replace(/\s+/g, ' ').trim();
  if (text) paragraphs.push(text);
}

return [{
  json: {
    fileName,
    paragraphs,
    text: paragraphs.join('\n\n')
  }
}];

// Collect the first non-empty input
for (const item of items) {
  if (item && Object.keys(item.json).length > 0) {
    return [item];
  }
}

// fallback: return empty if truly nothing was passed
return [{ json: { fileName: 'Unknown', text: '' } }];

return $input.all().map(item => {
  const b = item.binary || {};
  const k = Object.keys(b)[0];
  item.json.originalFileName =
    (k && b[k]?.fileName) || item.json.fileName || item.json.name || null;
  item.json.fileType = item.binary.data.fileExtension
  return item;
});

// Filter for explicitlyTrashed === true and aggregate IDs into a CSV
const ids = $input.all()
  .filter(item => item.json.explicitlyTrashed === true)
  .map(item => item.json.id);

// Join with commas (no quotes) for Supabase "In" condition
return [{
  json: {
    deletedIds: ids.join(',')
  }
}];

