const query = $json.query || "";

// Escape problematic quotes and normalize spacing
const escapedQuery = query
  .replace(/\\/g, "\\\\") // escape backslashes
  .replace(/"/g, '\\"')   // escape double quotes
  .replace(/\s+/g, " ")   // normalize multiple spaces
  .trim();

return {
  query: escapedQuery
};

/**
 * Keeps all fields from Hybrid Search results + adds rerank_index & rerank_score.
 */

const hybridDocs = $items('Trigger Hybrid Search', 0).map(i => i.json);

// Normalize Cohere Rerank payload
const rrPayload = $items('Cohere Rerank', 0)[0]?.json ?? {};
const rrObj = Array.isArray(rrPayload) ? rrPayload[0] : rrPayload;
const rrResults = Array.isArray(rrObj?.results) ? rrObj.results : [];

// Merge rerank data with full hybrid doc fields
const docsOrdered = rrResults
  .map(({ index, relevance_score }) => {
    const d = hybridDocs[index];
    if (!d) return null;
    return {
      ...d, // keep all original fields like file_name, doc_lang, vector_score, etc.
      rerank_index: index,
      rerank_score: relevance_score
    };
  })
  .filter(Boolean);

return [{ json: { docs_ordered: docsOrdered } }];

// This is the definitive script. It uses a robust, multi-pass approach
// with overlapping merges in both the initial pass and the final consolidation loop.

// --- CONFIGURATION ---
const MIN_CHUNK_LENGTH = 500;     // Chunks smaller than this will be merged.
const MAX_CHUNK_LENGTH = 2500;    // Chunks larger than this will be split.
// -------------------

const fullText = $('Combine Results').first().json.content;

// 1. Initial split into raw paragraphs.
const initialParagraphs = fullText.split(/\n\s*\n/)
    .filter(p => p.trim() !== '')
    .map(p => ({ text: p.trim(), isMerged: false }));

if (initialParagraphs.length === 0) {
    return [];
}

// ==========================================================
// 2. Initial Overlapping Merge Pass
// Short paragraphs are merged into BOTH neighbors.
// ==========================================================
for (let i = 0; i < initialParagraphs.length; i++) {
    if (initialParagraphs[i].text.length < MIN_CHUNK_LENGTH) {
        const runtText = initialParagraphs[i].text;

        if (i > 0) {
            initialParagraphs[i - 1].text += '\n\n' + runtText;
        }
        if (i < initialParagraphs.length - 1) {
            initialParagraphs[i + 1].text = runtText + '\n\n' + initialParagraphs[i + 1].text;
        }

        initialParagraphs[i].isMerged = true;
    }
}

const mergedParagraphs = initialParagraphs.filter(p => !p.isMerged).map(p => p.text);


// ==========================================================
// 3. Split Pass: Break up any chunks that are still too long.
// ==========================================================
let splitChunks = [];
for (const chunk of mergedParagraphs) {
    if (chunk.length > MAX_CHUNK_LENGTH) {
        const sentences = chunk.match(/[^\.!\?]+[\.!\?]+/g) || [chunk];
        for (const sentence of sentences) {
            if (sentence.trim()) {
                splitChunks.push(sentence.trim());
            }
        }
    } else {
        splitChunks.push(chunk);
    }
}


// ==========================================================
// 4. FINAL CONSOLIDATION LOOP (with Overlapping Merge)
// This cleans up any small chunks created by the splitting process.
// ==========================================================
let finalChunkTexts = splitChunks;
let didMergeInLoop = true;
while (didMergeInLoop && finalChunkTexts.length > 1) {
    didMergeInLoop = false;
    let smallestChunkIndex = -1;
    let smallestChunkSize = Infinity;

    // Find the smallest chunk below our minimum threshold
    for (let i = 0; i < finalChunkTexts.length; i++) {
        const len = finalChunkTexts[i].length;
        if (len < MIN_CHUNK_LENGTH && len < smallestChunkSize) {
            smallestChunkSize = len;
            smallestChunkIndex = i;
        }
    }

    if (smallestChunkIndex !== -1) {
        const chunkToMerge = finalChunkTexts.splice(smallestChunkIndex, 1)[0];

        // --- Overlapping Merge Logic ---
        const prevIndex = smallestChunkIndex - 1;
        const nextIndex = smallestChunkIndex; 

        // Merge with PREVIOUS if it exists
        if (finalChunkTexts[prevIndex]) {
            finalChunkTexts[prevIndex] += '\n\n' + chunkToMerge;
        }

        // Merge with NEXT if it exists
        if (finalChunkTexts[nextIndex]) {
            finalChunkTexts[nextIndex] = chunkToMerge + '\n\n' + finalChunkTexts[nextIndex];
        }

        didMergeInLoop = true; // Run the loop again to see if more merges are needed
    }
}


// ==========================================================
// 5. Final Formatting: Add character positions and format for output.
// ==========================================================
let runningCharIndex = 0;
const outputItems = finalChunkTexts.map((segmentText, index) => {
    // Using a running index is more reliable than searching, as merging creates non-unique text.
    const startChar = runningCharIndex;
    const endChar = startChar + segmentText.length;

    const item = {
        json: {
            segment_index: index,
            segment_text: segmentText.replace(/\s+/g, ' '),
            source_chars: {
                from: startChar,
                to: endChar
            }
        }
    };

    // Update the running index to the end of the current chunk plus any joining characters
    runningCharIndex = endChar + 2; // Assumes they are joined by '\n\n'
    return item;
});

return outputItems;

