// This node takes the chunk data and the LLM context and formats
// them into a single delimited string for each chunk.

// --- CONFIGURATION ---
// IMPORTANT: Change these names to match your N8N nodes.
const chunkerNodeName = 'Paragraph Chunking';
const llmContextNodeName = 'Add Context to Chunks';
// -------------------

const chunkerItems = $items(chunkerNodeName);
const llmItems = $items(llmContextNodeName);

if (chunkerItems.length !== llmItems.length) {
    throw new Error("Input data mismatch: The number of items from the chunker and LLM nodes are not equal.");
}

const formattedLines = [];

for (let i = 0; i < chunkerItems.length; i++) {
    const chunkerData = chunkerItems[i].json;
    // Adjust this path if your LLM output is structured differently
    const llmData = llmItems[i].json.output; 

    // Extract the data, providing default empty strings just in case
    const title = llmData.chunk_title || '';
    const context = llmData.chunk_context || '';
    const text = chunkerData.segment_text || '';
    const loc = chunkerData.source_chars || '';

    // Combine the fields into the desired format
    // const combinedString = `${title} ||| ${context} ||| ${text}`;
    const combinedString = `${context} ||| ${text}`;

    formattedLines.push({
        json: {
            // The final output for this item
            formatted_text: combinedString,
            metadata: {
              loc: loc,
              file_name: $('Combine Results').first().json.file_name,
              google_drive_id: $('Save File Name and Binary').first().json.id,
              file_type: $('Save File Name and Binary').first().json.fileType,
              category:$('Summarize and Add Metadata').first().json.output.category,
              timeframe: $('Summarize and Add Metadata').first().json.output.timeframe,
              lang: $('Summarize and Add Metadata').first().json.output.lang
            }
        }
    });
}

// The output is a list of items, each containing the formatted string.
return formattedLines;

const chunks = $('Merge Chunks and Metadata').all();
const embs   = $('Generate Embeddings for Chunks').all();

if (!chunks.length || !embs.length) {
  throw new Error('No chunks or embeddings to merge.');
}

// Build a lookup: chunk_index -> embedding
const embByIndex = new Map();

// Case A: single item that contains an array of embeddings
if (embs.length === 1 && Array.isArray(embs[0].json?.data)) {
  const arr = embs[0].json.data;
  for (let k = 0; k < arr.length; k++) {
    const idx = (arr[k].index ?? k);                      // OpenAI returns "index"
    const vec = arr[k].embedding ?? arr[k]?.data?.[0]?.embedding ?? null;
    if (vec) embByIndex.set(idx, Array.isArray(vec) ? vec.slice() : vec);
  }
} else {
  // Case B: one item per chunk
  for (const e of embs) {
    const idx =
      e.json?.chunk_index ??
      e.json?.index ??
      e.json?.data?.[0]?.index ??
      null;

    const vec =
      e.json?.data?.[0]?.embedding ??
      e.json?.embedding ??
      null;

    if (idx != null && vec) {
      embByIndex.set(idx, Array.isArray(vec) ? vec.slice() : vec);
    }
  }
}

const out = [];

for (let i = 0; i < chunks.length; i++) {
  const cj = chunks[i].json;

  // Prefer an explicit chunk_index; otherwise fall back to position i
  const idx = (cj?.chunk_index != null) ? cj.chunk_index : i;

  // Try by chunk_index, else by position
  let vector = embByIndex.get(idx);
  if (!vector && embs.length === chunks.length) {
    // Positional fallback if 1:1 count but missing indices
    const e = embs[i]?.json;
    vector = e?.data?.[0]?.embedding ?? e?.embedding ?? null;
  }

  if (!vector) {
    throw new Error(`No embedding found for chunk at output index ${i} (chunk_index=${cj?.chunk_index}).`);
  }

  // Optional sanity check: dimension (1536 for text-embedding-3-small)
  if (Array.isArray(vector) && vector.length !== 1536) {
    throw new Error(`Embedding length ${vector.length} != 1536 for chunk_index=${idx}`);
  }

  out.push({ json: { ...cj, embedding: vector } });
}

return out;
